mapper.update:
update fly_article
title=#{title}, content=#{content}, update_time=#{updateTime}, status=#{status},
where id=#{id}

update fly_article
status=#{status}, recommend=#{recommend},
where id=#{id}

update fly_article_category_merge
category_id=#{categoryId}, type_id=#{typeId},
where article_id=#{articleId}

mapper.select:
select count(1) from fly_article

	and title= #{title}
	and user_id = #{userId}
	and id != #{id}

select count(1) from fly_article_comment

	and article_id= #{articleId}
	and user_id = #{userId}
	and content = #{content}

select a.* ,c.* from fly_article a inner join fly_article_category_merge c on a.id=c.article_id

	and a.title LIKE CONCAT(CONCAT('%', #{title}),'%')
	and a.user_id=#{userId}
	and a.create_time BETWEEN STR_TO_DATE(#{createTime}, '%Y-%m-%d %H')
	and a.status >= 0
	and a.status = 0
	and a.status = 1
	and a.status = 2
	and a.status = 3

order by
a.${orderby}
${order}
limit #{offset},#{rows}

select * from fly_article_comment

	and article_id=#{articleId}
	and user_id=#{userId}
	and create_time BETWEEN STR_TO_DATE(#{createTime}, '%Y-%m-%d %H')
	and status >= 0
	and status = 0
	and status = 1
	and status = 2
	and status = 3

order by
${orderby}
${order}
limit #{offset},#{rows}
