mapper.select:
select * from fly_topic

	 and topic =  LIKE CONCAT(CONCAT('%', #{topic}),'%')
	and isgood = #{isgood}


			and to_days(create_time) = to_days(now())


			and yearweek(date_format(create_time,'%Y-%m-%d')) = yearweek(now());


			and date_format(create_time,'%Y-%m') = date_format(now(),'%Y-%m')


			and year(create_time) = year(NOW())


	and status >= 0
	and status = 0
	and status = 1
	and status = 2

order by
${orderBy}
${order}
limit #{offset},#{rows}

SELECT * FROM fly_topic AS t1 JOIN (SELECT ROUND(RAND() * ( (SELECT MAX(id) FROM fly_topic where id < 1000 )-(SELECT MIN(id) FROM fly_topic where id <1000 ))+(SELECT MIN(id) FROM fly_topic where id < 1000 )) AS id) AS t2
and t1.id >= t2.id and topic = LIKE CONCAT(CONCAT('%', #{topic}),'%') and isgood = #{isgood}
and to_days(create_time) = to_days(now())
and yearweek(date_format(create_time,'%Y-%m-%d')) = yearweek(now());
and date_format(create_time,'%Y-%m') = date_format(now(),'%Y-%m')
and year(create_time) = year(NOW())
and status >= 0 and status = 0 and status = 1
order by t1.${orderBy} ${order} limit #{offset},#{rows}

		select count(1) from (
			select id from fly_topic_info_merge

				and status >= 0
				and status = 0
				and status = 1
				and info_type=#{infoType}
				and topic_id=#{topicId}

            GROUP BY info_id order by id desc
		) f

select info_type,info_id from fly_topic_info_merge

    and status >= 0
    and status = 0
    and status = 1
    and status = 2
    and info_type=#{infoType}
    and topic_id=#{topicId}

GROUP BY info_id
order by
${orderBy}
${order}
limit #{offset},#{rows}
